const e=document.documentElement;e.classList.remove("dark");window.toggleTheme=()=>{e.classList.toggle("dark"),localStorage.setItem("theme",e.classList.contains("dark")?"dark":"light")};document.addEventListener("DOMContentLoaded",function(){const t=document.querySelector('.theme-switch input[type="checkbox"]'),n=()=>{document.documentElement.classList.toggle("dark"),document.documentElement.classList.contains("dark")&&localStorage.setItem("theme","dark")};t.addEventListener("click",()=>{n()})});
